const buttonA = <button>Histórico dos Clientes</button>;
const buttonB = <button>Cadastrar Cliente</button>;

const hasCustomer = false;

const App = () => {
  //sempre que possivel deixar separados em funções
  const renderShowHistory = () => (
    <div>
      Clique no botão abaixo para visualizar o histórico dos clientes: <br />
      {buttonA}
    </div>
  );

  const renderAddCustomer = () => (
    <div>
      Clique abaixo para cadastrar o cliente: <br />
      {buttonB}
    </div>
  );

  const showCustomer = () => {
    if (!hasCustomer) return null;
    return (
      <div>
        <h1>Nome do cliente: Fernando</h1>
      </div>
    );
  };

  //  ?  :  // operador ternario && ( //if in line
  // if/else line -> ? "" : ""
  return (
    <div>
      <p>Click Midia Digital</p>
      <p>Bem vindos!</p>

      {hasCustomer ? renderShowHistory() : renderAddCustomer()}
    </div>
  );
};

export default App;
