  return (
    <div>
      <p>Click Midia Digital</p>
      <p>Bem vindos!</p>

      {hasCustomer ? ( // && ( //if in line
        <div>
          Clique no botão abaixo para visualizar o histórico dos clientes:{" "}
          <br />
          {buttonA}
        </div>
      ) : (
        <div>
          Clique abaixo para cadastrar o cliente: <br />
          {buttonB}
        </div>
      )}
    </div>
  );
};

export default App;
